        //     ]
        //     // Generate a p tag for each element in the dataSet with the text: Subject: Count 
        //     d3.select('#pgraphs').selectAll('p').data(dataSet).enter().append('p').text(dt => dt.subject + ": " + dt.count)
        // }, [])

        return (
            <div className="skill-search" >
                <h1 className="title">Search for jobs you're interested in!</h1>
                <Select className="searchbar" options={options} isClearable={true} onChange={this.handleChange} />
                <button type="button" class="btn btn-outline-primary" onClick={this.onClick}
                // onAnimationEnd={() => this.setState({ fade: false })}
                // className={fade ? 'fade' : ''}
                >Search</button>
                {/* <svg ref={this.myDiv} id="scatter" width="800" height="500">
                    <text id="label" x="790" y="0" text-anchor="end" alignment-baseline="hanging"></text>
                </svg> */}
                {/* <div id="pgraphs"></div> */}
