import React, { Component } from "react";
import upload from "../images/upload.png";

export default class ResumeUpload extends Component {
  constructor(props) {
    super(props);

    this.state = {
      fileUploaded: null,
    };

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = (e) => {
    this.fileUploaded.current.click();
  };

  handleChange = (e) => {
    this.setState({ fileUploaded: e.target.files[0] }, () => {
      this.props.setFile(e.target.files[0]);
    });
  };

  render() {
    return (
      <div className="resume-upload">
        <p className="upload-resume-text">
          <b>Step 1:</b> Upload Your Resume
        </p>
        <p className="resume-small-text">file in .pdf</p>
        <div className="upload-button-container">
          <img src={upload} className="upload-image" />
          <p className="browse-text">
            <b>Browse</b> to Upload
          </p>
        </div>

        <input
          type="file"
          name="file"
          onChange={this.handleChange}
          ref={this.state.fileUploaded}
          accept=".pdf"
          style={{ display: "none" }}
        />
      </div>
    );
  }
}
