import React, { Component } from 'react';
import Select from 'react-select';
import * as d3 from 'd3';

const options = [
    { value: 'software_engineer', label: 'Software Engineer' },
    { value: 'mechanical_engineer', label: 'Mechanical Engineer' },
    { value: 'data_scientist', label: 'Data Scientist' }
]

export default class SkillSearch extends Component {
    constructor() {
        super();
        this.myDiv = React.createRef();
        this.state = {
            selectedOption: null,
            // fade: false

        }
    }

    handleChange = (selectedOption) => {
        this.setState({ selectedOption });
        console.log(this.state.selectedOption);
        // document.write(`Option selected:`, selectedOption.value); //this prints the selected option
    }


    onClick = () => {
        console.log(this.state.selectedOption);
        this.setState({ fade: true })
    }

    render() {
        const fade = this.state.fade

        // const svg = d3.select(this.myDiv);
        // console.log(svg)

        // svg.style("background-color", "blue");
        // const width = svg.attr("width");
        // const height = svg.attr("height");

        // const margin = { top: 20, right: 10, bottom: 30, left: 35 };
        // const chartWidth = width - margin.left - margin.right;
        // const chartHeight = height - margin.top - margin.bottom;

        let dataSet = [
            { subject: "Dogs", count: 150 },
            { subject: "Fish", count: 75 },
            { subject: "Cats", count: 135 },
            { subject: "Bunnies", count: 240 },
        ]
        // Generate a p tag for each element in the dataSet with the text: Subject: Count 
        d3.select('#pgraphs').selectAll('p').data(dataSet).enter().append('p').text(dt => dt.subject + ": " + dt.count)

        return (
            <div className="skill-search" >
                <h1 className="title">Search for jobs you're interested in!</h1>
                <Select className="searchbar" options={options} isClearable={true} onChange={this.handleChange} />
                <button type="button" class="btn btn-outline-primary" onClick={this.onClick}
                // onAnimationEnd={() => this.setState({ fade: false })}
                // className={fade ? 'fade' : ''}
                >Search</button>
                {/* <svg ref={this.myDiv} id="scatter" width="800" height="500">
                    <text id="label" x="790" y="0" text-anchor="end" alignment-baseline="hanging"></text>
                </svg> */}
                <div id="pgraphs"></div>
            </div>
        )
    }
}
