import React, { Component } from 'react';
import Select from 'react-select';
import * as d3 from 'd3';

const options = [
    { value: 'automation_engineer', label: 'Automation Engineer' },
    { value: 'computer_hardware_engineer', label: 'Computer Hardware Engineer' },
    { value: 'data_analyst', label: 'Data Analyst' },
    { value: 'data_engineer', label: 'Data Engineer' },
    { value: 'data_scientist', label: 'Data Scientist' },
    { value: 'electrical_engineer', label: 'Electrical Engineer' },
    { value: 'financial_engineer', label: 'Financial Engineer' },
    { value: 'machine_learning_engineer', label: 'Machine Learning Engineer' },
    { value: 'mechanical_engineer', label: 'Mechanical Engineer' },
    { value: 'network_engineer', label: 'Network Engineer' },
    { value: 'qa_engineer', label: 'QA Engineer' },
    { value: 'quantitative_analyst', label: 'Quantitative Analyst' },
    { value: 'quantitative_finance', label: 'Quantitative Finance' },
    { value: 'quantitative_research', label: 'Quantitative Research' },
    { value: 'quantitative_trading', label: 'Quantitative Trading' },
    { value: 'software_engineer', label: 'Software Engineer' },
    { value: 'systems_engineer', label: 'Systems Engineer' },
    { value: 'technology_analyst', label: 'Technology Analyst' }
]

export default class SkillSearch extends Component {
    constructor() {
        super();
        this.state = {
            selectedOption: null,
            chartExists: false,

            data: { "skill1": 5, "skill2": 8, "skill3": 4, "skill4": 1, "skill5": 9 },

            svg: null,
            chartWidth: null,
            chartHeight: null,
            chartArea: null,
            bottomAxis: null,
            bottomAxisG: null,
            leftAxis: null,
            leftAxisG: null

        }
        this.ref = React.createRef()
    }

    handleChange = (selectedOption) => {
        this.setState({ selectedOption });
        console.log(this.state.selectedOption);
    }


    onClick = () => {
        console.log(this.state.selectedOption);
        this.setState({ fade: true });
        this.drawChart();
    }

    onClick2 = () => {
        this.setState({ data: { "skill5": 1, "skill6": 2, "skill7": 3, "skill8": 4, "skill9": 5 } })
        console.log(this.state.data)
        // var svg = d3.select(this.ref.current)
        // svg.style('display', 'none');
    }

    componentDidMount = () => {
        const size = 400

        // if (this.state.chartExists) {
        //     console.log("exists");
        //     var svg = d3.select(this.ref.current);
        // }
        // else {
        //     console.log("does not exist");
        this.state.svg = d3.select(this.ref.current)
            .append('svg')
            .attr("class", "graph")
            .attr('width', size)
            .attr('height', size);

        //     this.setState({ chartExists: true })
        // }
        const width = this.state.svg.attr("width");
        const height = this.state.svg.attr("height");

        const margin = { top: 20, right: 10, bottom: 30, left: 35 };
        this.setState({ chartWidth: width - margin.left - margin.right });
        this.setState({ chartHeight: height - margin.top - margin.bottom });

        const annotations = this.state.svg.append("g").attr("id", "annotations");
        this.setState({
            chartArea: this.state.svg.append("g").attr("id", "chart")
                .attr("transform", "translate(" + margin.left + "," + margin.top + ")")
        });

        this.setState({ bottomAxis: d3.axisBottom() });
        this.setState({
            bottomAxisG: annotations.append("g")
                .attr("class", "x axis")
                .attr("transform", `translate(${margin.left},${350 + margin.top + 10})`)
        });

        this.setState({ leftAxis: d3.axisLeft() });
        this.setState({
            leftAxisG: annotations.append("g")
                .attr("class", "y axis")
                .attr("transform", `translate(${margin.left - 10},${margin.top})`)
        });

        // this.drawChart();
    }

    drawChart = () => {
        // console.log("TESTEWARGBTARGFVABDFZ")
        // console.log(bottomAxis);

        // const size = 400

        // if (this.state.chartExists) {
        //     console.log("exists");
        //     var svg = d3.select(this.ref.current);
        // }
        // else {
        //     console.log("does not exist");
        //     var svg = d3.select(this.ref.current)
        //         .append('svg')
        //         .attr("class", "graph")
        //         .attr('width', size)
        //         .attr('height', size);

        //     this.setState({ chartExists: true })
        // }


        // const width = svg.attr("width");
        // const height = svg.attr("height");

        // const margin = { top: 20, right: 10, bottom: 30, left: 35 };
        // const chartWidth = width - margin.left - margin.right;
        // const chartHeight = height - margin.top - margin.bottom;

        // let annotations = svg.append("g").attr("id", "annotations");
        // let chartArea = svg.append("g").attr("id", "chart")
        //     .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

        // let bottomAxis = d3.axisBottom()
        // let bottomAxisG = annotations.append("g")
        //     .attr("class", "x axis")
        //     .attr("transform", `translate(${margin.left},${chartHeight + margin.top + 10})`)

        // let leftAxis = d3.axisLeft()
        // let leftAxisG = annotations.append("g")
        //     .attr("class", "y axis")
        //     .attr("transform", `translate(${margin.left - 10},${margin.top})`)

        // var skills = []
        // var frequency = []
        var res = []
        for (const [key, value] of Object.entries(this.state.data)) {
            // skills.push(key);
            // frequency.push(value);
            res.push({
                'skill': key,
                'frequency': value
            })
        }
        console.log(res);

        // function updateChart(res) {
        // const skillExtent = d3.extent(skills)
        const skillExtent = d3.map(res, d => d.skill)
        console.log(skillExtent)

        const frequencyExtent = d3.extent(res, d => d.frequency)
        console.log(frequencyExtent)

        // const genres = d3.map(data, d => d.genre)
        const skillScale = d3.scaleBand().domain(skillExtent).range([0, this.state.chartWidth])
            .padding(0.05);

        console.log(skillExtent)
        this.state.bottomAxis.scale(skillScale)
        this.state.bottomAxisG.transition().call(this.state.bottomAxis);

        const frequencyScale = d3.scaleLinear().domain(frequencyExtent).range([this.state.chartHeight, 0]);

        this.state.leftAxis.scale(frequencyScale)
        this.state.leftAxisG.transition().call(this.state.leftAxis);

        console.log(res);
        this.state.chartArea.selectAll('rect.bar').data(res) // d => d.genre
            .join(enter => enter.append('rect')
                .attr('class', 'bar')
                .attr("fill", "steelblue")
                .attr("x", d => skillScale(d.skill))
                .attr("y", d => frequencyScale(d.frequency))
                .attr("height", d => frequencyScale(0) - frequencyScale(d.frequency) - 100)
                .attr("width", skillScale.bandwidth())
                .attr("opacity", 0) // Set opacity low, then animate to 1 to make them fade in
                .call(enter => enter.transition().attr('opacity', 1)),
                update => update.call(update => update.transition()  // Animate resizing and movement
                    .attr("fill", "steelblue")
                    .attr("x", d => skillScale(d.skill))
                    .attr("y", d => frequencyScale(d.frequency))
                    .attr("height", d => frequencyScale(0) - frequencyScale(d.frequency))
                    .attr("width", skillScale.bandwidth())),
                exit => exit.call(exit => exit.transition().attr('opacity', 0).remove())); // Animate opacity to fade out
        // }

        // updateChart(res);
        // const rectWidth = 95
        // svg.selectAll('rect')
        //     .data(this.state.data)
        //     .enter()
        //     .append('rect')
        //     .attr('x', (d, i) => 5 + i * (rectWidth + 5))
        //     .attr('y', (d) => size - d)
        //     .attr('width', rectWidth)
        //     .attr('height', (d) => d)
        //     .attr('fill', 'tomato')
        // 
        // this.state.data.forEach(d => {
        //     // d['points'] = Number(d['points']);
        //     // d['price'] = Number(d['price'].replace(/,/g, "").replace(/\$/g, ""));
        //     console.log(d);
        // });

    }



    render() {

        return (
            <div className="skill-search-container" >
                <h1 className="title">Search for jobs you're interested in!</h1>
                <p className="temp">testing</p>
                <div className="skill-search">
                    <Select className="searchbar" options={options} isClearable={true} onChange={this.handleChange} />
                    <button type="button" class="btn btn-outline-primary" onClick={this.onClick} disabled={!this.state.selectedOption}
                    // onAnimationEnd={() => this.setState({ fade: false })}
                    // className={fade ? 'fade' : ''}
                    >Search</button>
                </div>
                <button className="change1" onClick={this.onClick2}>change1</button>
                <div className="chart" ref={this.ref} />
            </div>
        )
    }
}
