import React, { Component } from "react";
import upload from "../images/upload.png";

export default class ResumeUpload extends Component {
  constructor(props) {
    super(props);

    this.state= {
      fileUploaded: React.useRef(null)
    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = (e) => {
    this.fileUploaded.current.click();
  };

  handleChange = (e) => {
    this.setState
    this.props.setFile(e.target.files[0])
  };

  render() {
    return (
      <div className="resume-upload">
        <p className="upload-resume-text">
          <b>Step 1:</b> Upload Your Resume
        </p>
        <p className="resume-small-text">file in .pdf</p>
        <div onClick={this.handleClick}>
        <p>
          <b>Browse</b> to Upload
        </p>
        </div>

        <input
          type="file"
          name="file"
          onChange={(e) => this.props.setFile(e.target.files[0])}
          ref = {this.state.selectedFile}
          value={this.state.selectedFile}
          accept=".pdf"
          style={{display:'none'}}
        />

      </div>
    );
  }
}
